import "./index.css";

// Operador condicional ternario
const activo = true;

const mensaje = activo ? "Activo" : "Inactivo";

console.log(mensaje);

// sin necesidad del "else"
const condicion = true;

const unico = condicion && "Activo: unico";

console.log(unico);
